INSERT INTO `moban` (`mbid`, `mbname`, `mbstr`, `mbcode`, `addtime`, `mbstat`) VALUES (3, '常用二', '3', '<fieldset class="brcolor" style="text-align: -webkit-auto; white-space: normal; margin: 0px; padding: 5px 15px; border: 1px solid rgb(127, 63, 191); max-width: 100%; color: rgb(62, 62, 62); font-family: 宋体; line-height: 24px; orphans: 2; widows: 2; box-shadow: rgb(165, 165, 165) 1px 2px 1px; background-color: rgb(253, 253, 253); border-top-left-radius: 1em !important; border-top-right-radius: 1em !important; border-bottom-right-radius: 1em !important; border-bottom-left-radius: 1em !important; word-wrap: break-word !important; box-sizing: border-box !important;"><legend style="padding: 0px; max-width: 100%; margin: 0px; color: rgb(0, 0, 0); word-wrap: break-word !important; box-sizing: border-box !important;"><span style="max-width: 100%; margin: 0px; padding: 0px; color: rgb(165, 165, 165); font-family: arial, helvetica, sans-serif; word-wrap: break-word !important; box-sizing: border-box !important;"><strong style="max-width: 100%; color: rgb(102, 102, 102); word-wrap: break-word !important; box-sizing: border-box !important;"><span class="bkcolor" style="max-width: 100%; margin: 0px 8px 0px 0px; padding: 4px 10px; border-top-left-radius: 5px!important; border-top-right-radius: 5px!important; border-bottom-right-radius: 5px!important; border-bottom-left-radius: 5px!important; color: rgb(255, 251, 240); word-wrap: break-word !important; box-sizing: border-box !important; background: rgb(127, 63, 191);font-size: 14px;">三花清凉茶</span></strong></span></legend><p style="margin-top: 0px; margin-bottom: 12px; max-width: 100%; min-height: 1.5em; white-space: pre-wrap; padding: 0px; line-height: 2em; word-wrap: break-word !important; box-sizing: border-box !important;"><span style="line-height: 24px; text-align: -webkit-auto;">主要成分：金银花2克、枸杞子2克、甘草1克、黄贡菊2朵、白贡菊1朵。</span><br/></p><p>用途：具有清热解毒、平肝明目的作用。适用于压力大而至情绪容易激动，面部烘热，口燥咽乾的人群。</p><p><span style="text-align: -webkit-auto;">用法：沸水冲泡、泡淡为止。</span></p></fieldset><p style="white-space: normal;"><br/></p><fieldset class="brcolor" style="text-align: -webkit-auto; white-space: normal; margin: 0px; padding: 5px 15px; border: 1px solid rgb(127, 63, 191); max-width: 100%; color: rgb(62, 62, 62); font-family: 宋体; line-height: 24px; orphans: 2; widows: 2; box-shadow: rgb(165, 165, 165) 1px 2px 1px; background-color: rgb(253, 253, 253); border-top-left-radius: 1em !important; border-top-right-radius: 1em !important; border-bottom-right-radius: 1em !important; border-bottom-left-radius: 1em !important; word-wrap: break-word !important; box-sizing: border-box !important;"><legend style="padding: 0px; max-width: 100%; margin: 0px; color: rgb(0, 0, 0); word-wrap: break-word !important; box-sizing: border-box !important;"><span style="max-width: 100%; margin: 0px; padding: 0px; color: rgb(165, 165, 165); font-family: arial, helvetica, sans-serif; word-wrap: break-word !important; box-sizing: border-box !important;"><strong style="max-width: 100%; color: rgb(102, 102, 102); word-wrap: break-word !important; box-sizing: border-box !important;"><span class="bkcolor" style="max-width: 100%; margin: 0px 8px 0px 0px; padding: 4px 10px; border-top-left-radius: 5px!important; border-top-right-radius: 5px!important; border-bottom-right-radius: 5px!important; border-bottom-left-radius: 5px!important; color: rgb(255, 251, 240); word-wrap: break-word !important; box-sizing: border-box !important; background: rgb(127, 63, 191);font-size: 14px;">双花洋参饮</span></strong></span></legend><p>主要原料：西洋参2克、金银花2克、贡菊2朵、麦冬3克、甘草2克。</p><p>用途：具有清热、益气、养阴的作用，适用于易疲劳，易上火人士。</p><p>用法：沸水冲泡，泡淡为止。</p></fieldset><p style="white-space: normal;"><br/></p><p><br/></p><p><br/></p>', '2015-01-09 00:44:30', '');
INSERT INTO `moban` (`mbid`, `mbname`, `mbstr`, `mbcode`, `addtime`, `mbstat`) VALUES (4, '多图文', '', '<p id="topic_content" class="answer_text" style="margin-top: 0px; margin-bottom: 0px; max-width: 100%; word-wrap: normal; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, 微软雅黑, Arial, sans-serif; line-height: 25.600000381469727px; box-sizing: border-box !important; background-color: rgb(255, 255, 255);"><img src="http://weituibao.qiniudn.com/ueditor/php/upload/image/20141013/1413191780892298.gif" title="1410250013899265.gif" alt="5457.gif"/></p><p style="margin-top: 0px; margin-bottom: 0px; max-width: 100%; word-wrap: normal; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, 微软雅黑, Arial, sans-serif; line-height: 25.600000381469727px; box-sizing: border-box !important; background-color: rgb(255, 255, 255);"><strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important;"><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; font-size: 19px; font-family: 宋体; color: rgb(0, 176, 240);">如果您觉得我们的内容很赞，请记得点击页面右上角按钮收藏，转发或分享到朋友圈哦。</span></strong><strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; "><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; font-size: 19px; font-family: 宋体; color: rgb(0, 176, 240);">好东西记得跟朋友分享嘛</span></strong><strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; "><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important;">^-^</span></strong></p><p style="margin-top: 0px; margin-bottom: 0px; max-width: 100%; word-wrap: normal; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, 微软雅黑, Arial, sans-serif; line-height: 25.600000381469727px; box-sizing: border-box !important; background-color: rgb(255, 255, 255);"><br/></p><fieldset class="brcolor" style="WORD-WRAP: break-word !important; MAX-WIDTH: 100%; BORDER-TOP: rgb(192,80,77) 1px dotted; FONT-FAMILY: 宋体; BORDER-RIGHT: rgb(192,80,77) 1px dotted; BORDER-BOTTOM: rgb(192,80,77) 1px dotted; PADDING-BOTTOM: 20px; PADDING-TOP: 20px; PADDING-LEFT: 20px; MIN-HEIGHT: 1.5em; BORDER-LEFT: rgb(192,80,77) 1px dotted; LINE-HEIGHT: 2em; PADDING-RIGHT: 20px; border-bottom-right-radius: 15px; border-bottom-left-radius: 10px"><legend style="max-width: 100%; font-family: 微软雅黑; padding: 0px; text-align: center; margin: 0px; word-wrap: break-word !important;border:none;"><p class="bkcolor" style="word-wrap: normal; font-size: 14px; max-width: 100%; word-break: normal; color: rgb(255, 255, 255); padding: 0px 20px 4px; margin-top: 0px; margin-bottom: 0px; min-height: 1.5em; line-height: 2em; border-bottom-right-radius: 100%!important; border-bottom-left-radius: 100%!important; background-color: rgb(192, 80, 77);"><strong class="ue_t" style="MAX-WIDTH: 100%">今日寒露</strong></p></legend><p style="word-wrap: normal; max-width: 100%; word-break: normal; padding: 0px; margin-top: 0px; margin-bottom: 0px; min-height: 1.5em; line-height: 2em;"><span style="color: rgb(89, 89, 89); background-color: rgb(255, 255, 255); font-family: Helvetica, 宋体; line-height: 28px; white-space: pre-wrap; -webkit-text-size-adjust: none; ">古代把露作为天气转凉变冷的标志。白露节气“露凝而白”，至寒露时已是“露气寒冷，将凝结”为霜了。寒露后已是深秋，天气开始转冷。露水更多，气温更低。</span><br/><span class="ue_t" style="WORD-WRAP: break-word !important; MAX-WIDTH: 100%; LINE-HEIGHT: 2em;margin:0;"></span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; min-height: 1em; color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: normal; box-sizing: border-box !important; white-space: pre-wrap; "><br style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; "/><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(127, 127, 127); "></span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; min-height: 1em; color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: normal; box-sizing: border-box !important; white-space: pre-wrap; "><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(227, 108, 9); "><strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; ">寒露。是24节气中最早出现“寒”字的节气，</strong></span>是夏阳入土的最后阶段，之后寒霜雪（霜降、小大雪）节节紧扣，离立冬还有1个月。</p></fieldset><p><br/></p><blockquote class="brcolor" style="WORD-WRAP: break-word; FONT-SIZE: 14px; MAX-WIDTH: 100%; VERTICAL-ALIGN: baseline; PADDING-BOTTOM: 15px; PADDING-TOP: 15px; PADDING-LEFT: 25px; MARGIN: 0px; BORDER-LEFT: rgb(102,102,102) 10px solid; DISPLAY: block; LINE-HEIGHT: 24px; TOP: 0px; PADDING-RIGHT: 25px; BACKGROUND-COLOR: rgb(238,238,238);"><strong style="color: rgb(227, 108, 9); font-family: Helvetica, 宋体; line-height: 28px; white-space: pre-wrap; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; ">【深秋滋阴润五脏】木瓜银耳猪骨汤</strong></blockquote><p style="text-align: center; "><span style="color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; white-space: pre-wrap; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); "><img src="http://weituibao.qiniudn.com/ueditor/php/upload/image/20141013/1413191781863951.jpg!editor.jpg" title="1413191359248960.jpg" alt="0 (1).jpg"/></span></p><p><span style="color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; white-space: pre-wrap; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); ">主打的木瓜清心润肺、健胃益脾；银耳、百合这些材料就不多说了，一个秋冬都要吃的。</span><span style="font-family: Helvetica, 宋体; line-height: 28px; white-space: pre-wrap; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(227, 108, 9); "><strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; ">喝一碗，秋燥鼻子冒火的感觉没了，那种怎么喝水都喝不透的感觉才真正消除。<span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; "><strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; ">木瓜含有丰富的木瓜酵素。经常熬夜的人应多吃木瓜</strong></span><span style="color: rgb(62, 62, 62); ">，弥补体内的酵素。或者用木瓜来做酵素也非常应季。</span></strong></span><br/></p><p><span style="font-family: Helvetica, 宋体; line-height: 28px; white-space: pre-wrap; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(227, 108, 9); "><strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; "><span style="color: rgb(62, 62, 62); "></span></strong></span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; min-height: 1em; color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: normal; box-sizing: border-box !important; white-space: pre-wrap; "><strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; "><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(127, 127, 127); ">做法：</span></strong></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; min-height: 1em; color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: normal; box-sizing: border-box !important; white-space: pre-wrap; "><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(127, 127, 127); ">1、把干货材料提前泡半小时，猪骨飞水，洗干净后把除木瓜外的所有材料放入汤煲内，加水大火煮开后转小火慢煲；</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; min-height: 1em; color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: normal; box-sizing: border-box !important; white-space: pre-wrap; "><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(127, 127, 127); ">2、1小时后，切木瓜，去皮去籽，我喜欢把靠近籽的那一层内膜也去掉，这样汤煲出来就没有木瓜的异味了。</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; min-height: 1em; color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: normal; box-sizing: border-box !important; white-space: pre-wrap; "><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(127, 127, 127); ">3、木瓜放入，煲软后即可关火。这个汤甜甜的不适合放盐调味。可以备一碟生抽蘸猪骨吃。</span></p><p><br/></p><blockquote class="brcolor" style="white-space: normal; word-wrap: break-word; font-size: 14px; max-width: 100%; vertical-align: baseline; padding: 15px 25px; margin: 0px; border-left-color: rgb(102, 102, 102); border-left-width: 10px; border-left-style: solid; line-height: 24px; top: 0px; background-color: rgb(238, 238, 238); "><strong style="color: rgb(227, 108, 9); font-family: Helvetica, 宋体; line-height: 28px; white-space: pre-wrap; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; ">【丝瓜络除风湿痛风】</strong></blockquote><p style="white-space: normal; text-align: center; "><br/></p><p style="white-space: normal; text-align: center; "><img src="http://weituibao.qiniudn.com/ueditor/php/upload/image/20141013/1413191782357258.jpg!editor.jpg" title="1413191772363643.jpg" alt="0 (2).jpg"/></p><p style="white-space: normal; "><span style="color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; white-space: pre-wrap; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); "></span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; min-height: 1em; color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: normal; box-sizing: border-box !important; white-space: pre-wrap; ">深秋的老丝瓜起络了，家里有痛风病人的可以收一些丝瓜络来。丝瓜络与橘络一样，能通络脉。<strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; "><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(227, 108, 9); ">很多病邪躲在人体细微处，就像藏在角落里的灰尘，而橘络和丝瓜络擅长钻进这些细小的地方进行清扫。</span></strong>橘络偏于疏通络脉中的痰淤，而丝瓜络偏于清除络脉中的风湿。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; min-height: 1em; color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: normal; box-sizing: border-box !important; white-space: pre-wrap; "><br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; min-height: 1em; color: rgb(62, 62, 62); font-family: Helvetica, 宋体; line-height: 28px; -webkit-text-size-adjust: none; background-color: rgb(255, 255, 255); max-width: 100%; word-wrap: normal; box-sizing: border-box !important; white-space: pre-wrap; "><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; color: rgb(227, 108, 9); "><strong style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; ">【给痛风患者的丝瓜络茶】</strong></span><span style="max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; font-size: 15.5555562973022px; color: rgb(227, 108, 9); ">有关节炎、痛风的人可用丝瓜络煮水来调理。</span>把丝瓜络洗净，冷水下锅煮1小时代茶饮即可，对痛风病人有很好的效果。这可不是黑暗料理哦！</p><p style="white-space: normal; "><span style="color: rgb(127, 127, 127); "><strong style="color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, 微软雅黑, Arial, sans-serif; line-height: 25.600000381469727px; white-space: pre-wrap; max-width: 100%; word-wrap: break-word !important; box-sizing: border-box !important; ">做法：</strong><span style="background-color: rgb(255, 255, 255); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, 微软雅黑, Arial, sans-serif; line-height: 25.600000381469727px; white-space: pre-wrap; "> </span><br/></span></p><p style="margin-top: 0px; margin-bottom: 0px; max-width: 100%; word-wrap: normal; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, 微软雅黑, Arial, sans-serif; line-height: 25.600000381469727px; box-sizing: border-box !important; background-color: rgb(255, 255, 255);"><span style="color: rgb(127, 127, 127); ">1、净老鸭和瘦猪肉分别切成3cm见方的块。</span></p><p style="margin-top: 0px; margin-bottom: 0px; max-width: 100%; word-wrap: normal; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, 微软雅黑, Arial, sans-serif; line-height: 25.600000381469727px; box-sizing: border-box !important; background-color: rgb(255, 255, 255);"><span style="col